import React, { useState } from "react";
// import "./CreateJourneyPage.css";

function CreateJourneyPage() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send the new journey data to the server (using a mock function here)
    const newJourney = { title, description };
    fetch("/api/journeys", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newJourney),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Journey created:", data);
        // Redirect to home or newly created journey
      })
      .catch((error) => console.error("Error creating journey:", error));
  };

  return (
    <div className="create-journey-page">
      <h1>Create a New Journey</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>
        <button type="submit">Create Journey</button>
      </form>
    </div>
  );
}

export default CreateJourneyPage;
