import React, { useState } from "react";
import "../styles/CreateJourneyPage.css";

function CreateJourneyPage() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [userId, setUserId] = useState(""); // Add state for user_id
  const [image, setImage] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("location", location);
    formData.append("user_id", userId); // Assuming userId is available
    if (image) {
      formData.append("image", image);
    }

    fetch("/api/journeys", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Journey created:", data);
        // Redirect to home or newly created journey
      })
      .catch((error) => console.error("Error creating journey:", error));
  };

  return (
    <div className="create-journey-page">
      <h1>Create a New Journey</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="image">Upload Image (Optional)</label>
          <input
            type="file"
            id="image"
            accept=".jpg,.jpeg,.png,.gif"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>
        <button type="submit">Create Journey</button>
      </form>
    </div>
  );
}

export default CreateJourneyPage;
