import React, { useState } from "react";
import "../styles/CreateJourneyPage.css";
import { useNavigate } from "react-router-dom";
import PlacesAutocomplete from "react-places-autocomplete";

function CreateJourneyPage() {
  const navigate = useNavigate();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("location", location);

    if (image) {
      formData.append("image", image);
    }

    fetch("/api/journeys", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Journey created:", data);
        navigate("/");
      })
      .catch((error) => console.error("Error creating journey:", error));
  };

  const handleSelect = (address) => {
    setLocation(address);
  };

  return (
    <div className="create-journey-page">
      <h1>Create a New Journey</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="location">Location</label>
          <PlacesAutocomplete
            value={location}
            onChange={setLocation}
            onSelect={handleSelect}
          >
            {({
              getInputProps,
              suggestions,
              getSuggestionItemProps,
              loading,
            }) => (
              <div>
                <input
                  {...getInputProps({
                    placeholder: "Search Location ...",
                    className: "location-search-input",
                  })}
                  id="location"
                  required
                />
                <div className="autocomplete-dropdown-container">
                  {loading && <div>Loading...</div>}
                  {suggestions.map((suggestion) => {
                    const className = suggestion.active
                      ? "suggestion-item--active"
                      : "suggestion-item";
                    return (
                      <div
                        {...getSuggestionItemProps(suggestion, { className })}
                        key={suggestion.placeId}
                      >
                        <span>{suggestion.description}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </PlacesAutocomplete>
        </div>

        <div className="form-group">
          <label htmlFor="image">Upload Image (Optional)</label>
          <input
            type="file"
            id="image"
            accept=".jpg,.jpeg,.png,.gif"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>
        <button type="submit">Create Journey</button>
      </form>
    </div>
  );
}

export default CreateJourneyPage;
