import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../styles/JourneyCard.css";
import WeatherWidget from "../components/WeatherWidget";

function JourneyPage() {
  const { id } = useParams();
  const [journey, setJourney] = useState({
    title: "Exploring the Ancient Ruins of Machu Picchu",
    date: "2023-08-12",
    description:
      "A breathtaking adventure through the Andes Mountains to uncover the lost city of the Incas. The hike was challenging but incredibly rewarding with stunning views at every turn.",
    locations: [
      {
        name: "Cusco",
        coordinates: { lat: -13.532, lng: -71.967 },
        notes: "Spent a day acclimatizing and exploring the historic city.",
      },
      {
        name: "Sacred Valley",
        coordinates: { lat: -13.295, lng: -72.122 },
        notes: "Visited ancient Incan sites and local markets.",
      },
      {
        name: "Machu Picchu",
        coordinates: { lat: -13.163, lng: -72.545 },
        notes:
          "Finally reached Machu Picchu at sunrise. The view was absolutely breathtaking.",
      },
    ],
    photos: [
      {
        url: "https://example.com/machu-picchu.jpg",
        caption: "Sunrise over Machu Picchu",
      },
      {
        url: "https://example.com/sacred-valley.jpg",
        caption: "Exploring the Sacred Valley",
      },
    ],
  });

  // useEffect(() => {
  //   // Fetch the journey by id from the server (using a mock fetch function here)
  //   fetch(`/api/journeys/${id}`)
  //     .then((response) => response.json())
  //     .then((data) => setJourney(data))
  //     .catch((error) => console.error("Error fetching journey:", error));
  // }, [id]);

  // if (!journey) {
  //   return <div>Loading...</div>;
  // }

  return (
    <div className="journey-page">
      <h1>{journey.title}</h1>
      <p>{journey.description}</p>

      {/* <div className="journey-images">
        {journey.images.map((image, index) => (
          <img key={index} src={image.url} alt={`Journey ${index + 1}`} />
        ))}
      </div> */}
      {/* <div className="journey-map"> */}
      {/* Placeholder for map integration */}
      {/* <p>Map displaying journey locations will be here.</p>
      </div> */}
      {/* <WeatherWidget location={journey.location} /> */}
    </div>
  );
}

export default JourneyPage;
