import React, { useState, useEffect } from "react";
import JourneyCard from "../components/JourneyCard";
import "../styles/HomePage.css";

function HomePage() {
  const [journeys, setJourneys] = useState([
    {
      id: 1,
      title: "Exploring the Alps",
      description: "A journey through the breathtaking landscapes of the Alps.",
      image: "/img/Alps.jpeg",
    },
    {
      id: 2,
      title: "Safari in Kenya",
      description: "An unforgettable safari adventure in the heart of Kenya.",
      image: "/img/Kenya.jpeg",
    },
    {
      id: 3,
      title: "Cultural Tour of Japan",
      description: "Discover the rich culture and history of Japan.",
      image: "/img/Japan.jpg",
    },
  ]);

  useEffect(() => {
    // Fetch journeys from the server (using a mock fetch function here)
    fetch("/api/journeys")
      .then((response) => response.json())
      .then((data) => setJourneys(data))
      .catch((error) => console.error("Error fetching journeys:", error));
  }, []);

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete all data?")) {
      fetch("/api/delete", {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("All data deleted:", data);
        })
        .catch((error) => console.error("Error deleting data:", error));
    }
  };

  return (
    <div className="home-page">
      <h1>Welcome to the world of travel!</h1>
      <p>
        Here you can find people who are excited about nature, stories about
        experiences during a trip, exciting moments along the way, get to know
        new cultures and interesting people.
      </p>

      <p>We would love to hear your story too :)</p>
      <div className="journey-list">
        {journeys.map((journey) => (
          <JourneyCard key={journey.id} journey={journey} />
        ))}
      </div>
      <button onClick={handleDelete}>Delete All Data</button>
    </div>
  );
}

export default HomePage;
